import React, {Component} from 'react';
import { connect } from 'react-redux';
import './sorting.css';

class Sorting extends Component{
    constructor(props){
        super(props);
        //this.sortByPriceAsc = this.sortByPriceAsc.bind(this);
        this.sortByPriceDesc = this.sortByPriceDesc.bind(this);
    }

    sortByPriceAsc = () => {
        this.setState(prevState => {
            this.props.items.sort((a,b) => a.price - b.price)
        });
    }

    sortByPriceAsc() {
       this.setState(prevState => {
         this.props.items.sort((a, b) => (a.price - b.price))
     });
     }

     sortByPriceDesc() {
       this.setState(prevState => {
         this.props.items.sort((a, b) => (b.price - a.price))
     });
     }

render(){console.log(this.props.items);
    return(
        <>
        <strong>Sort By</strong> 
        <ul>
            <li onClick={() => this.sortByPriceAsc}>Price -- High Low</li>
            <li onClick={this.sortByPriceDesc}>Price -- Low High</li>
            <li>Discount</li>
        </ul>
    </>
    )
}
}
const mapStateToProps = (state) => ({items:state.items});

export default connect(mapStateToProps)(Sorting);
