import React, { Component } from 'react';
import { connect } from 'react-redux'
import { addToCart } from './actions/cartActions'

 class Home extends Component{

    componentDidMount() {
        fetch('https://api.myjson.com/bins/qzuzi')
        .then(res => res.json())
        .then((data) => {
            this.props.getProductList(data);
        })
        .catch(console.log)
      }

    
    handleClick = (id)=>{
        this.props.addToCart(id); 
    }

    render(){
        let itemList = this.props.items.map(item=>{
            return(
                <div className="col-sm-4 mb-4" key={item.id}>
                        <div className="card">
                            <img src={item.img} alt={item.title} className="img-fluid" />
                            <div className="card-body">
                                <h5 className="card-title">{item.title}</h5>
                                <div className="price-row clearfix">
                                    <div className="priceWithDiscount float-left">&#8377; {item.price- ((item.price*10)/100)}</div> 
                                    <div className="price float-left">{item.price}</div>
                                    <div className="discount text-right">{10} % off</div>
                                </div>
                            </div>
                            <div className="text-center mb-3">
                                <button className="btn btn-primary" onClick={()=>{this.handleClick(item.id)}}>Add to Cart</button>
                            </div>
                        </div>                        
                 </div>

            )
        })

        return(
            <div className="container">
                <h3 className="center">Our items</h3>
                <div className="row">
                    <div className="col-sm-3">

                    </div>
                    <div className="col-sm-9">
                        <div className="row">
                            {itemList}
                        </div>
                        
                    </div>
                    
                </div>
            </div>
        )
    }
}
const mapStateToProps = (state)=>{
    return {
      items: state.items
    }
  }
const mapDispatchToProps= (dispatch)=>{
    
    return{
        addToCart: (id)=>{dispatch(addToCart(id))}
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Home)
