import React, { Component } from 'react';
import { connect } from 'react-redux';
import './sorting.css';

class Sorting extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: this.props.items
        }
    }



    render() {
        const {sortByPriceAsc} = this.props;
        return (
            <>
                <strong>Sort By</strong>
                <ul>
                    <li onClick={sortByPriceAsc}>Price -- Low High</li>
                    <li onClick={sortByPriceDesc}>Price -- High Low</li>
                    <li onClick={sortByDiscount}>Discount</li>
                </ul>
            </>
        )
    }
}
const mapStateToProps = (state) => ({ items: state.items });

export default connect(mapStateToProps)(Sorting);
