import React, { Component } from 'react';
import InputRange from 'react-input-range';
import { connect } from 'react-redux';
import './rangeslider.css';
 
class RangeSlider extends Component {
  constructor(props) {
    super(props);
 
    this.state = {
      value: { min: 100, max: 1000 },
    };
  }
 
  render() {
      const {rangePriceFilter} = this.props;
      console.log('from Range',this.props);
    return (
        <>
      <InputRange
        maxValue={10000}
        minValue={10}
        value={this.state.value}
        onChange={value => this.setState({ value })} />
        <div className="text-center">
        <button className="btn btn-primary rounded" onClick={rangePriceFilter}>Apply</button>
        </div>
        </>
    );
  }
}

const mapStateToProps = (state) => ({ items: state.items });

export default connect(mapStateToProps)(RangeSlider);
