import React, { Component } from 'react';
import { connect } from 'react-redux';
import './sorting.css';

class Sorting extends Component {
    render() {
        const {sortByPriceAsc, sortByPriceDsc, sortByDiscount} = this.props;
        return (
            <>
                <strong className="float-left">Sort By</strong>
                <ul>
                    <li onClick={sortByPriceAsc}>Price -- Low High</li>
                    <li onClick={sortByPriceDsc}>Price -- High Low</li>
                    <li onClick={sortByDiscount}>Discount -- Hight Low</li>
                </ul>
            </>
        )
    }
}
const mapStateToProps = (state) => ({ items: state.items });

export default connect(mapStateToProps)(Sorting);
