import React, { Component } from 'react';
import { connect } from 'react-redux';
class Recipe extends Component{
    render(){
            console.log('from recipe ', this.props);
        return(
            <div className="container">
                <div className="cartPrice-container">
                <div className="totalHeading"><strong>PRICE DETAILS</strong></div>
                <div className="items clearfix mb-2"><span>{`Price (${this.props.addedItems.length}) Item`}</span> <span>:</span> <span className="float-right">&#8377;{this.props.totalPrice}</span></div>
                <div className="discount clearfix pb-2 border-bottom"><span>Discount</span> <span>:</span> <span className="float-right">&#8377;{this.props.totalDiscount}</span></div>
                <div className="orderTotal clearfix mt-2"><strong>Total Payable</strong>  <strong className="float-right">&#8377;{(this.props.totalCostPrices).toFixed(2)}</strong></div>
    </div>
                    
                 </div>
        )
    }
}

const mapStateToProps = (state)=>{
    return{
        addedItems: state.addedItems,
        total: state.total
    }
}

// const mapDispatchToProps = (dispatch)=>{
//     return{
//         addShipping: ()=>{dispatch({type: 'ADD_SHIPPING'})},
//         substractShipping: ()=>{dispatch({type: 'SUB_SHIPPING'})}
//     }
// }

export default connect(mapStateToProps)(Recipe)
