import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';



 const Navbar = (addedItems) => {
    console.log(addedItems);
    const totalQty = addedItems && addedItems.length && addedItems.map(o => o.quantity).reduce((a, c) => { return a + c })
    return(
            <nav className="nav-wrapper mb-3">
                <div className="container">
                    <div className="row">
                        <div className="col-sm-3">
                            <Link to="/" className="brand-logo">Shopping</Link>
                        </div>
                        <div className="col-sm-9">
                            <ul className="float-right headerRight">
                            <li><Link to="/cart"><i className="fa fa-shopping-cart"></i><sup style={{color:'white'}}>{totalQty}</sup></Link></li>
                            </ul>
                        </div>
                    </div>                    
                </div>
            </nav>
   
        
    )
}

const mapStateToProps = (state) => {
    return {
        addedItems: state.addedItems.addedItems
    }
  }

export default connect(mapStateToProps)(Navbar);
