import React, {Component} from 'react';
import { connect } from 'react-redux';
import './sorting.css';

class Sorting extends Component{
    constructor(props){
        super(props);
        this.state = {
            items: this.props.items
        }
    }

    sortByPriceAsc=()=>{

        let sortedProductsAsc;
        sortedProductsAsc= this.state.items.sort((a,b)=>{
           return parseInt(a.price)  - parseInt(b.price);
        })

        this.setState({
            items:sortedProductsAsc
        })
    }

    sortByPriceDsc=()=>{

        let sortedProductsDsc;
        sortedProductsDsc= this.state.items.sort((a,b)=>{
           return parseInt(b.price)  - parseInt(a.price);
        })

        this.setState({
            items:sortedProductsDsc
        })
    }

render(){console.log(this.state.items);
    return(
        <>
        <strong>Sort By</strong> 
        <ul>
            <li onClick={this.sortByPriceAsc}>Price -- Low High</li>
            <li onClick={this.sortByPriceDesc}>Price -- High Low</li>
            <li>Discount</li>
        </ul>
    </>
    )
}
}
const mapStateToProps = (state) => ({items:state.items});

export default connect(mapStateToProps)(Sorting);
